function PlotGroupsInSpace(Beads,GroupAssignment)

NumClusters=max(GroupAssignment);

myjet=[0,0,0.5625;0         0    0.6250;         0         0    0.6875;         0         0    0.7500;         0         0    0.8125;         0         0    0.8750;         0         0    0.9375;         0         0    1.0000;         0    0.0625    1.0000;         0    0.1250    1.0000;         0    0.1875    1.0000;         0    0.2500    1.0000;         0    0.3125    1.0000;         0    0.3750    1.0000;         0    0.4375    1.0000;         0    0.5000    1.0000;         0    0.5625    1.0000;         0    0.6250    1.0000;         0    0.6875    1.0000;         0    0.7500    1.0000;         0    0.8125    1.0000;         0    0.8750    1.0000;         0    0.9375    1.0000;         0    1.0000    1.0000;    0.0625    1.0000    0.9375;    0.1250    1.0000    0.8750;    0.1875    1.0000    0.8125;    0.2500    1.0000    0.7500;    0.3125    1.0000    0.6875;    0.3750    1.0000    0.6250;    0.4375    1.0000    0.5625;    0.5000    1.0000    0.5000;    0.5625    1.0000    0.4375;    0.6250    1.0000    0.3750;    0.6875    1.0000    0.3125;    0.7500    1.0000    0.2500;    0.8125    1.0000    0.1875;    0.8750    1.0000    0.1250;    0.9375    1.0000    0.0625;    1.0000    1.0000         0;    1.0000    0.9375         0;    1.0000    0.8750         0;    1.0000    0.8125         0;    1.0000    0.7500         0;    1.0000    0.6875         0;    1.0000    0.6250         0;    1.0000    0.5625         0;    1.0000    0.5000         0;    1.0000    0.4375         0;    1.0000    0.3750         0;    1.0000    0.3125         0;    1.0000    0.2500         0;    1.0000    0.1875         0;    1.0000    0.1250         0;    1.0000    0.0625         0;    1.0000         0         0;    0.9375         0         0;    0.8750         0         0;    0.8125         0         0;    0.7500         0         0;    0.6875         0         0;    0.6250         0         0;    0.5625         0         0;    0.5000         0         0];
myjet=myjet(randperm(size(myjet,1)),:);
Multiplier=floor(length(myjet)/NumClusters);

figure(2)
clf
for i=1:max(GroupAssignment)
    BeadsInThisGroup=Beads(GroupAssignment==i);
    
    color=myjet(Multiplier*(i-1)+1,:);

    for k=1:length(BeadsInThisGroup)
        rectangle('Position',[BeadsInThisGroup(k).Locations(1),BeadsInThisGroup(k).Locations(2),20,20],...
          'Curvature',[1,1], 'FaceColor',color,'EdgeColor','None')
    end

end
